import React, { useState } from 'react';
import { StyleSheet, Text, View, Button } from 'react-native';

export default function App() {
  let index = true;
  let about = false;
  let contact = false;

  const [outputText, setOutputText] = useState('Yo');
  function setAbout() {
    about = !about;
    setOutputText('This app is my first approach to React Native');
  }
  function setContact() {
    about = !about;
    setOutputText('Aaron Moser');
  }

  return (
    <View style={styles.container}>
      <Text>{outputText}</Text>

      <Text onPress={setAbout}>About <Text>Contact</Text></Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
